import React from 'react'
import styles from './styles.module.scss'
import SwiperCore, { Pagination } from 'swiper';
import { Swiper, SwiperSlide } from 'swiper/react';
import MainSliderItem from "./Item";
import clsx from 'clsx';

SwiperCore.use([Pagination]);

const MainSlider = () => {
  return (
    <section className={clsx(styles.main_slider, 'container')}>
      <Swiper
        spaceBetween={0}
        slidesPerView={1}
        pagination={{ el: '.main_slider_pagination', type: 'bullets', clickable: true }}
        loop={true}
      >
        <SwiperSlide>
          <MainSliderItem images={"<img src='/slide1.png' alt='slide1' />"} />
          <MainSliderItem images={"<img src='/slide2.png' alt='slide2' />"} />
        </SwiperSlide>

        <div className={'main_slider_pagination container'} />
      </Swiper>
    </section>
  )
}

export default MainSlider
