import React from 'react'
import Head from 'next/head'
import AdminLayout from '../../components/AdminLayout'
import AdminNewsComponent from '../../components/AdminNewsComponent'
import AdminWebinarsComponent from '../../components/AdminWebinarsComponent'


function AdminPanel({project, news}) {
console.log("AdminPanel -> project", project)
console.log("AdminPanel -> news", news)

  return (
    <AdminLayout>
      <Head>
        <title>Tourism: AdminPanel</title>
        <meta name="viewport" content='width=1640' />
      </Head>
          
        <div className="container">
          <h6 className="adminText">Добро пожалловать в систему управления «Туризм онлайн»</h6>

          <AdminNewsComponent title={'Новости'} data={news} smalltitle={'новость'} link={'/admin/news'} bool={true} />
          <AdminNewsComponent title={'Публикации'} data={project} smalltitle={'публикацию'} link={'/admin/project'} bool={true} />
          <AdminWebinarsComponent title={'Вебинары'} link={'/admin/webinars'} bool={true} />
        </div>
          
    </AdminLayout>
  )

}

export async function getServerSideProps(context) {

  const new_res = await fetch(`http://127.0.0.1:8000/api/news`)
  const news = await new_res.json()

  const res = await fetch(`http://127.0.0.1:8000/api/projects`)
  const project = await res.json()

  
  return { props: { project, news } }
}

export default AdminPanel
