import React from 'react'
import Head from 'next/head'
import RegistrationLayout from '../../components/RegistrationLayout'

import styles from './styles.module.scss'
import clsx from 'clsx'

export default function registration() {

  const [visiblePopup, setVisiblePopup] = React.useState(false);
  const [activeItem, setActiveItem] = React.useState(0);

  const allLang = ['ЭЦП', 'Логин']

  const onSelectItem = id => {
    setActiveItem(id)
  }


  return (
    <RegistrationLayout>
      <Head>
        <title>Tourism: Online</title>
      </Head>
      <div className={styles.registration}>
        <h6>Вход в систему</h6>
        <div className={styles.registration_btn}>
          {
            allLang && (
              allLang.map((el,i) => (
                <button  
                  className={clsx(activeItem === i ? styles.active : '')}
                  onClick={() => activeItem !== i ? onSelectItem(i) : ''}
                  key={i}
                >
                  {el}
                </button>
              ))
            )
          }
        </div>
        <div className={clsx(styles.registration_bg, 'border-radius')}>
          <div className={clsx(styles.registration_download, 'border-radius')}>
            <div>
              <img src="/download.svg" alt="#"/>
              Загрузить
            </div>
          </div>
        </div>
      </div>
    </RegistrationLayout>
  )
}
