import React from 'react'
import Head from 'next/head'
import AdminLayout from '../../../components/AdminLayout'
import AdminEditsComponent from '../../../components/AdminEditsComponent'


export default function insideAdminNews({news, photo}) {

  return (
    <AdminLayout>
      <Head>
        <title>Tourism: AdminPanel</title>
      </Head>
        <div className="container">
          <AdminEditsComponent title={'Добавление новости'} data={news} img={photo} link={'/admin'} save={false} />
        </div>
    </AdminLayout>
  )
}

export async function getServerSideProps(context) {
  const BASE_URL = process.env.NEXT_PUBLIC_BASE_URL
  const {id} = context.query

  const new_res = await fetch(`${BASE_URL}/api/news/${id}`)
  const news = await new_res.json()

  const photoImg = await fetch(`${BASE_URL}/storage/${news.data.photo.photo}`)
  const photo = await photoImg.json()
  
  return { props: { news,photo } }
}
