import React from 'react'
import Link from 'next/link'

import styles from './styles.module.scss'


export default function AdminEditsComponent({title = 'Title'}) {

  const [visiblePopup, setVisiblePopup] = React.useState(false);
  const [activeItem, setActiveItem] = React.useState(0);

  const allLang = ['RU', 'KZ']
  const activeLang = allLang[activeItem]


  const toggleVisible = () => {
    setVisiblePopup(!visiblePopup)
  }
  
  const onSelectItem = id => {
    setActiveItem(id)
    setVisiblePopup(false)
  }

  return(
    <div className={styles.edits}>
      <div className={styles.edits_head}>
        <h2>{title}</h2>
        <Link href="/">
          <a className={styles.edits_link}><span>Назад</span> <img src="/back.png" alt="back"/></a>
        </Link>
      </div>
      <div className={styles.edits_btn}>
        {allLang.map((el,id) => (
          <button key={id}>
            {el}
          </button>
        ))}
      </div>
    </div>
  )



}
